import 'package:flutter/material.dart';

class NGO {
  final String name;
  final String function;
  final String logoUrl;

  NGO({required this.name, required this.function, required this.logoUrl});
}

class NGOPage extends StatelessWidget {
  final List<NGO> ngos = [
    NGO(
      name: "Amnesty International",
      function:
          "Protecting human rights worldwide, advocating for fair trials, ending impunity for human rights abuses, and promoting justice and accountability.",
      logoUrl:
          "data:image/png;base64,",
    ),
    NGO(
      name: "Human Rights Watch",
      function:
          "Investigating and reporting on human rights abuses globally, advocating for justice, accountability, and the rule of law.",
      logoUrl:
          "data:image/jpeg;base64,",
    ),
    NGO(
      name: "CREA",
      function:
          "CREA is a feminist human rights organization based in India that focuses on advancing the rights of women and marginalized communities.",
      logoUrl:
          "data:image/png;base64,",
    ),
    NGO(
      name: "MAKAAM",
      function:
          "•	MAKAAM facilitates networking and collaboration among grassroots women's organizations to amplify their voices and strengthen their collective impact.",
      logoUrl:
          "data:image/jpeg;base64,",
    ),

    // Add more NGOs here...
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xffd5bc78),
      appBar: AppBar(
        title: Text(
          "NGOs for Law and Justice",
          style: TextStyle(color: Colors.black),
        ),
        backgroundColor: Color(0xffdcab23),
      ),
      body: ListView.builder(
        itemCount: ngos.length,
        itemBuilder: (context, index) {
          return NGOCard(ngo: ngos[index]);
        },
      ),
    );
  }
}

