        child: Column(
          children: [
            Container(
              width: 100,
              height: 100,
              decoration: BoxDecoration(
                shape: BoxShape.circle,
                image: DecorationImage(
                  image: NetworkImage(ngo.logoUrl),
                  fit: BoxFit.cover,
                ),
              ),
            ),
            SizedBox(height: 8.0),
            Text(
              ngo.name,
              style: TextStyle(
                fontSize: 20,
                fontWeight: FontWeight.bold,
              ),
              textAlign: TextAlign.center,
            ),
            SizedBox(height: 4.0),
            Text(
              ngo.function,
              style: TextStyle(fontSize: 16),
              textAlign: TextAlign.center,
            ),
            SizedBox(height: 8.0),
            ElevatedButton(
              onPressed: () {
                // Add your action when the button is pressed
              },
              style: ElevatedButton.styleFrom(
                primary:
                    Color(0xffdcab23), // Change button background color here
              ),
              child: Text(
                "Join",
                style: TextStyle(
                  color: Colors.black,
                ),
              ),
            ),
          ],
        ),
